      <div >
          <div style={{marginTop:"100px",display:"flex"}}>
              <img src={process.env.PUBLIC_URL+'/girl.jpg'} style={{height:"50px",width:"80px",borderRadius:"50%"}} alt="dp" />
              {/* <i class="fas fa-user-circle" style={{height:"100px",width:"120px"}}></i> */}
             <textarea style={{width:"100%"}}/>
             <button>Post</button>
          </div>
      </div>
    )
}
export default Comment;
