    const [posts ,setPost]=useState([]);
    const [loading, setLoading] = useState(false);
    useEffect(() => {
        const fetchPost =async()=>{
           const res= await axios.get("/posts")
        //    setPost(res.data);
        console.log(res);
        }
        fetchPost();
       
    }, []);
    if (loading) {
        return <p>Data is loading...</p>;
      }
