import "./setting.css";
import Sidebar from "../../component/sidebar/Sidebar";
import { useContext } from "react";
import { Context } from "react";


export default function Setting() {
    const {user}=useContext(Context);
    const [file, setFile] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newPost = {
            username: user.username,
            title,
            desc,

        };
    if (file) {
        const data = new FormData();
        const filename = Date.now() + file.name;
        data.append("name", filename);
        data.append("file", file);
        newPost.photo = filename;

        try {
            await axios.post("/upload", data);
        } catch (err) {

        }
    }
    try {
        const res = await axios.post("/posts", newPost);
        window.location.replace("/post/" + res.data._id);

    } catch (err) {

    }

};
    return (
        <div className="setting" >
          <div className="settingwrapper">
              <div className="settingtitle">
                  <span className="settingupdate">Update  account</span>
                  <span className="settingdelete">Delete Account</span>
              </div>
              <form  className="settingform">
                  <label>Profile Picture</label>
                  <div className="settingprofilepic">
                      <img src={ user.profilePic} alt="" />
                    <label htmlFor="fileinput">
                    <i class=" settingprofileicon fas fa-user-circle"></i>
                    </label>
                    <input type="file" name="" id="fileinput" style={{display:"none"}}/>
                  </div>
                  <label>Username</label>
                  <input type="text" placeholder="safak" />
                  <label>Email</label>
                  <input type="email" placeholder="safak@gmail.com" />
                  <label >Password</label>
                  <input type="password" placeholder="safak" />
                 <button className="settingsubmit">Update</button>
              </form>

          </div>
              <Sidebar />
            
        </div>
    )
}
