import "./post.css"
import { Link } from "react-router-dom";


export default function Post({ post }) {
    const PF="localhost:1000/images/";
    return (
        <div classname="post">
            {post.photo &&(
           <img src={PF+ post.photo}  class="postimg" />
             )}
             {/* /<</di</div>/div>/<img src={process.env.PUBLIC_URL+"/comp2.jgp"} alt="" /> */}
        {/* <div classname="post" > */}
            {/* <img src={process.env.PUBLIC_URL + "/comp1.jpg"} class="postimg" /> */}
            <div className="postinfo">
                <div className="postcats">
                    {post.category.map((c) => {

                        <span className="postcat">{c.name}</span>
                    })}

                </div>
                <Link className="link" to={`/post/${post._id}`}>
                <span className="posttitle">{post.title}</span></Link>
                <hr /><span className="postdate">{new Date(post.createdAt).toDateString()}</span>
            </div>
            <p className="postdes">
                {post.desc}
            </p>
        </div>
    )
}
