import React from 'react'
import { useState } from 'react';


const initialvalue= {
    name:"",
    id:'',
    comment:"",
    date:new Date(),

}
const Comment=()=> {
    const [comment,setComment]=useState(initialvalue);
    const handleChange=(e)=>{
        setComment({
            ...comment,
            name:"safak",
            id:"safakid",
            comment:e.target.value
        })
    }

    return (
      <div >
          <div style={{marginTop:"100px",display:"flex"}}>
              {/* <img src={process.env.PUBLIC_URL+'/girl.jpg'} style={{height:"50px",width:"50px",borderRadius:"50%"}} alt="dp" /> */}
              <i class="fas fa-user-circle" style={{height:"200px"}}></i>
             <textarea style={{width:"100%",margin:"0 20px"}} rows="3" onChange={(e)=>handleChange(e)}/>
             
             <button style={{background:"lightblue",color:"var(--color-primary)",height:"20px"}}>Post</button>
          </div>
      </div>
    )
}
export default Comment;
