import {useEffect } from "react";
import { useLocation } from"react-router";
import axios from"axios";

export default function Singlepost() {

    const location= useLocation();
    console.log(location);
    const path=location.pathname.split("/")[2];
    const [post,setPost]=useState({})

    useEffect(() => {
     const getPost=async()=>{
         const res=await axios.get("/posts/"+path)
console.log(res);
setPost(res.data);
     };
        getPost();
    }, [path])

    return (
        <div className="singlepost">
           <div className="singlepostwrapper">
               {post.photo &&(
               <img src={post.photo} style={{height:"450px", width:"100%"}} alt="" className="singlepostimg" />
               )}
               <h1 className="singlepostitle">{post.title}
               <div className="singlepostedit">
               <i className="singleposticon far fa-edit"></i>
               <i className="singleposticon far fa-trash-alt"></i>
               </div>
               </h1>
          
           <div className="singlepostinfo">
               <span className="singlepostauthor">Author:<b>{post.username}</b></span>
               <span className="singlepostdate">1 hour ago </span>
           </div>
           <p className="singlepostdes">Lorem ipsum dolor sit amet consectetur adipisicing elit. Id eaque totam unde inventore quia repellat recusandae ratione quae facilis dmf,fjkf,jkjfmv m d
