import "./write.css";
import { useContext, useState } from "react";
import axios from "axios";
import {Context} from "../../context/Context";

export default function Write() {
    const [title,setTitle]=useState("")
    const [desc,setDesc]=useState("")
    const [file,setFile]=useState(null);
    const {user} =useContext(Context);


    const handleSubmit=async(e)=>{
        e.preventDefault();
        const newPost={
            username:user.username,
title,
desc,

        };
        if(file){
            const data=new FormData;
            const filename=Date.now()+file.name;
            data.append("name",filename);
            data.append("file",file);
            newPost.photo=filename;
        
        try{
await axios.post("/upload",data);
        }catch(err){

        }
    }
    try{
    const res= await axios.post("/posts/",newPost);
    window.localStorage.replace("/post/"+res.data._id);

    }catch(err){

    }
         
    }
    return (
        <div className="write">
            {file && 
            (<img src={URL.createObjectURL(file)} className="writeimg" />
            )}
            <form action="" className="writeform" onSubmit={handleSubmit}>
                <div className="writeformgrp" >
                    <label htmlFor="fileinput">
                    <i className="writeicon fas fa-plus"></i>
                    </label>
                  <input type="file" name="" id="fileinput" style={{display:"none"}} onChange={e=>setFile(e.target.files[0])}/>
                  <input type="text" name="" id=""  placeholder="Title" className="writeinput" autoFocus={true}/>             
                     </div>
                     <div className="writeformgrp">
                         <textarea  placeholder="Tell your story..." type="text" className
                         ="writeinput writetext"></textarea>
                     </div>
                     <button className="writesubmit" type="submit">Publish</button>
            </form>
            </div>
    )
}
