import "./post.css"
import { Link } from "react-router-dom";

export default function Post({ post }) {
    const [loading, setLoading] = useState(false);
    if (loading) {
        return <p>Data is loading...</p>;
      }
    return (

        <div className="post" > { post.photo &&
            <img src={post.photo} class="postimg" />
        }

            {/* <img src={process.env.PUBLIC_URL+"/comp2.jpg"} className="postimg" /> */}
            <div className="postinfo">
                <div className="postcats">
                    <ul>
                    { post.categories.map((c) => (
                        <span className="postcat"><li key={c.id}>{c.username}</li></span>
                    ))}</ul>
                </div>
                <Link to={`/post/${post._id}`} className="link">
                    <span className="posttitle">{post.title}</span></Link>
                <hr /><span className="postdate">{new Date(post.createdAt).toDateString}</span>
            </div>
            <p className="postdes">
                {post.desc}
            </p>
        </div>

    )
}
